mapper.select:
select
rec_header_id, stu_id, score, email_send, create_by, creat_time, update_by, update_time
from eams.eams_record_header


        and rec_header_id = #{recHeaderId}


        and stu_id = #{stuId}


        and score = #{score}


        and email_send = #{emailSend}


        and create_by = #{createBy}


        and creat_time = #{creatTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
