mapper.select:
select
stu_id, stu_name, stu_num, prof_id, sex, email, phone_num, password, id_card, status, salt, create_by,
create_time, update_by, update_time
from eams.eams_student s


        and stu_id = #{stuId}


        and stu_name = #{stuName}


        and stu_num = #{stuNum}


        and prof_id = #{profId}


        and sex = #{sex}


        and email = #{email}


        and phone_num = #{phoneNum}


        and password = #{password}


        and id_card = #{idCard}


        and status = #{status}


        and salt = #{salt}


        AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


        AND (s.prof_id = #{profId} OR s.prof_id IN ( SELECT p.prof_id FROM eams_profession p WHERE FIND_IN_SET
        (#{profId},ancestors) ))
